    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        try {
    Class.forName("com.mysql.cj.jdbc.Driver");
    String url = "jdbc:mysql://localhost/dasauto";
    String user = "root";
    String password = "12345";

    // Using try-with-resources for automatic resource management
    try (Connection conn = DriverManager.getConnection(url, user, password);
         PreparedStatement pstmt = conn.prepareStatement("SELECT * FROM staff_details WHERE STAFF_ID = ?")) {
        
        pstmt.setString(1, jTextField1.getText().trim());
        try (ResultSet rset = pstmt.executeQuery()) {
            if (rset.next()) {
                jTextField2.setText(rset.getString("FIRST_NAME"));
                jTextField3.setText(rset.getString("LAST_NAME"));
                jTextField4.setText(rset.getString("AADHAR_NUMBER"));

                // Get and handle the gender value
                String gender = rset.getString("GENDER");
                jRadioButton1.setSelected(false);
                jRadioButton2.setSelected(false);
                jRadioButton3.setSelected(false);

                if (gender != null) {
                    switch (gender.trim().toUpperCase()) {
                        case "MALE":
                            jRadioButton1.setSelected(true);
                            break;
                        case "FEMALE":
                            jRadioButton2.setSelected(true);
                            break;
                        case "OTHERS":
                            jRadioButton3.setSelected(true);
                            break;
                        default:
                            System.out.println("Unexpected gender value: " + gender);
                            break;
                    }
                } else {
                    System.out.println("Gender is null for STAFF_ID: " + jTextField1.getText());
                }

                jTextField5.setText(rset.getString("EMAIL_ID"));
                jTextField6.setText(rset.getString("PHONE_NUMBER"));
            } else {
                JOptionPane.showMessageDialog(null, "STAFF NOT FOUND");
            }
        }
    }
} catch (Exception e) {
    JOptionPane.showMessageDialog(null, e.getMessage());
}




    }//GEN-LAST:event_jButton1ActionPerformed

