    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Connection conn;
PreparedStatement pst;
try {
    // Load the MySQL driver and establish a connection
    conn = DriverManager.getConnection("jdbc:mysql://localhost/dasauto", "root", "12345");

    // Prepare the SQL statement to insert data into admin_details
    String SQL = "INSERT INTO admin_details (STAFF_ID, FIRST_NAME, LAST_NAME, AADHAR_NUMBER, GENDER, EMAIL_ID, PHONE_NUMBER, PASSWORD_) " +
                 "VALUES (?, ?, ?, ?, ?, ?, ?, ?)";

    pst = conn.prepareStatement(SQL);

    // Set the parameters for the prepared statement using data from the text fields
    pst.setString(1, jTextField0.getText()); // Assuming COLUMN1 corresponds to this field
    pst.setString(2, jTextField1.getText()); // COLUMN2
    pst.setString(3, jTextField2.getText()); // COLUMN3
    pst.setString(4, jTextField3.getText()); // COLUMN4
    pst.setString(5, jTextField4.getText()); // COLUMN5
    pst.setString(6, jTextField5.getText()); // COLUMN6
    pst.setString(7, jTextField6.getText()); // COLUMN7
    pst.setString(8, jTextField7.getText()); // COLUMN8

    // Execute the query and check if the account is created
    int cnt = pst.executeUpdate();

    if (cnt > 0) {
        JOptionPane.showMessageDialog(null, "Account created");
    } else {
        JOptionPane.showMessageDialog(null, "Sorry, account not created");
    }
} catch (Exception e) {
    // Display any errors that occur during the process
    JOptionPane.showMessageDialog(null, e.getMessage());
}


    }//GEN-LAST:event_jButton1ActionPerformed

