    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
     Connection conn;
        Statement st;
        ResultSet rset;
        try{
            Class.forName("java.sql.DriverManager");
            conn = DriverManager.getConnection("jdbc:mysql://localhost/dasauto","root","12345");
            st=conn.createStatement();
            String SQL ="INSERT INTO staff_details VALUES('"+jTextField0.getText()+"','"+jTextField1.getText()+"','"+jTextField2.getText()+"','"+jTextField3.getText()+"','"+jTextField4.getText()+"','"+jTextField5.getText()+"','"+jTextField6.getText()+"','"+jTextField7.getText()+"';";
            int cnt=st.executeUpdate(SQL);
            if(cnt>0) {
                JOptionPane.showMessageDialog(null,"account created");
            } else
                JOptionPane.showMessageDialog(null,"Sorry ACCOUNT NOT CREATED");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e.getMessage());
        }

    }//GEN-LAST:event_jButton1ActionPerformed

