    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
            Connection conn;
            Statement st;
            ResultSet rset;
                try{
                    Class.forName("java.sql.DriverManager");
                    conn = DriverManager.getConnection("jdbc:mysql://localhost/dasauto","root","12345");
                    st=conn.createStatement();
                    if(jRadioButton1.isSelected())
                    {
                        String SQL ="UPDATE car_sedan SET STAT_US="+"'"+jTextField2.getText()+"'"+" WHERE CAR_ID="+"'"+jTextField1.getText()+"'";
                    int a = st.executeUpdate(SQL);
                    if(a>0){
                    JOptionPane.showMessageDialog(null,"STATUS UPDATED SUCCESSFULLY");
                    }
                    else
                        JOptionPane.showMessageDialog(null,"CAR NOT FOUND");

                    st.close();
                    conn.close();
                }
 else if(jRadioButton2.isSelected())
                    {
                        String SQL ="UPDATE car_suv SET STAT_US="+"'"+jTextField2.getText()+"'"+" WHERE CAR_ID="+"'"+jTextField1.getText()+"'";
                    int a = st.executeUpdate(SQL);
                    if(a>0){
                    JOptionPane.showMessageDialog(null,"STATUS UPDATED SUCCESSFULLY");
                    }
                    else
                        JOptionPane.showMessageDialog(null,"CAR NOT FOUND");
                    
                    st.close();
                    conn.close();

        }
 else if (jRadioButton3.isSelected())
                    {
                         String SQL ="UPDATE car_business_class SET STAT_US="+"'"+jTextField2.getText()+"'"+" WHERE CAR_ID="+"'"+jTextField1.getText()+"'";
                    int a = st.executeUpdate(SQL);
                    if(a>0){
                    JOptionPane.showMessageDialog(null,"STATUS UPDATED SUCCESSFULLY");
                    }
                    else
                        JOptionPane.showMessageDialog(null,"CAR NOT FOUND");

                    st.close();
                    conn.close();
                }
 else if(jRadioButton4.isSelected()){
                         String SQL ="UPDATE car_micro SET STAT_US="+"'"+jTextField2.getText()+"'"+" WHERE CAR_ID="+"'"+jTextField1.getText()+"'";
                    int a = st.executeUpdate(SQL);
                    if(a>0){
                    JOptionPane.showMessageDialog(null,"STATUS UPDATED SUCCESSFULLY");
                    }
                    else
                        JOptionPane.showMessageDialog(null,"CAR NOT FOUND");

                    st.close();
                    conn.close();
                }
                     else if(jRadioButton5.isSelected()){
                         String SQL ="UPDATE booking_details SET STAT_US="+"'"+jTextField2.getText()+"'"+" WHERE CAR_ID="+"'"+jTextField1.getText()+"'";
                    int a = st.executeUpdate(SQL);
                    if(a>0){
                    JOptionPane.showMessageDialog(null,"STATUS UPDATED SUCCESSFULLY");
                    }
                    else
                        JOptionPane.showMessageDialog(null,"CAR NOT FOUND");

                    st.close();
                    conn.close();
                }
        }
                catch (Exception e) {
                    JOptionPane.showMessageDialog(null, e.getMessage());
                }
    }//GEN-LAST:event_jButton1ActionPerformed

