    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
       Connection conn;
PreparedStatement pst;
ResultSet rset;
try {
    conn = DriverManager.getConnection("jdbc:mysql://localhost/dasauto", "root", "12345");

    // Escaping the PASSWORD_ column using backticks to avoid conflicts
    String SQL = "INSERT INTO customer_details (CUSTOMER_ID, FIRST_NAME, LAST_NAME, AADHAR_NUMBER, GENDER, EMAIL_ID, PHONE_NUMBER, PASSWORD) " +
                 "VALUES (?, ?, ?, ?, ?, ?, ?, ?)";

    pst = conn.prepareStatement(SQL);

    pst.setString(1, jTextField0.getText()); // CUSTOMER_ID
    pst.setString(2, jTextField1.getText()); // FIRST_NAME
    pst.setString(3, jTextField2.getText()); // LAST_NAME
    pst.setString(4, jTextField3.getText()); // AADHAR_NUMBER
    pst.setString(5, jTextField4.getText()); // GENDER
    pst.setString(6, jTextField5.getText()); // EMAIL_ID
    pst.setString(7, jTextField6.getText()); // PHONE_NUMBER
    pst.setString(8, jTextField7.getText()); // PASSWORD_

    int cnt = pst.executeUpdate();

    if (cnt > 0) {
        JOptionPane.showMessageDialog(null, "Account created");
    } else {
        JOptionPane.showMessageDialog(null, "Sorry, account not created");
    }
} catch (Exception e) {
    JOptionPane.showMessageDialog(null, e.getMessage());
}

    }//GEN-LAST:event_jButton1ActionPerformed

